import React from 'react';
import { Sparklines, SparklinesLine, SparklinesBars } from 'react-sparklines';
import './Overview.css';

const Overview = () => {


  const maxRaisedThisMonth = Math.max(...dataRaisedThisMonth);
  const maxAvailablePayout = Math.max(...dataAvailablePayout);

  const raisedThisMonth = 5405;
  const availablePayout = 2218;
  const newSupporters = 72;
  const newFollowers = 297;

  // Example data for sparklines
  const dataRaisedThisMonth = [5, 10, 5, 20, 8, 15, 10];
  const dataAvailablePayout = [3, 12, 5, 18, 9, 4];
  const dataNewSupporters = [1, 3, 2, 4, 3, 7];
  const dataNewFollowers = [8, 17, 13, 23, 18, 6];

  const renderBarGraph = (data, max) => (
    <div className="bar-graph-container">
      {data.map((value, index) => (
        <div key={index} className="bar" style={{ height: `${(value / max) * 100}%` }} />
      ))}
    </div>
  );


  return (
    <div>
      <h3>Overview</h3>
      <div className='overview'>
        <div>
          <h2>Raised This Month</h2>
          {/* <Sparklines data={dataRaisedThisMonth} limit={6}>
            <SparklinesBars color="#9e4ef7" />
          </Sparklines> */}
          {renderBarGraph(dataRaisedThisMonth, maxRaisedThisMonth)}
          <p>${raisedThisMonth}</p>
        </div>
        <div>
          <h2>Available Payout</h2>
          <Sparklines data={dataAvailablePayout} limit={6}>
            <SparklinesBars color="blue" />
          </Sparklines>
          <p>${availablePayout}</p>
        </div>
        <div>
          <h2>New Supporters</h2>
          <Sparklines data={dataNewSupporters} limit={6}>
            <SparklinesLine color="blue" />
          </Sparklines>
          <p>{newSupporters}</p>
        </div>
        <div>
          <h2>New Followers</h2>
          <Sparklines data={dataNewFollowers} limit={6}>
            <SparklinesLine color="blue" />
          </Sparklines>
          <p>{newFollowers}</p>
        </div>
      </div>
    </div>
  );
};

export default Overview;
